import './profileUpdate.css';
import React, { useState, useEffect } from 'react';

import { Navigate , useNavigate  } from "react-router-dom";

import {fetchUser} from '../../redux/action/authAction'
import U_CONTROLLER from '../../Controllers/User.Controller';
import { Link } from 'react-router-dom';
import CONFIG from '../../Controllers/Config.Controller';



function Profile(){
	

	

	const[userData , setUserData] = useState(null);

	useEffect(()=> {
		const token = sessionStorage.getItem('token')
		async function getUser() {
			const userdata =await fetchUser(token);
			console.log("asasas" , userdata);
			setUserData(userdata.data)
		}
		getUser();
		console.log("uD" , userData);
	}, [])

	const onDelete = async () => {
        const data = {
            id: userData?._id,
            name: userData?.email,
        }
        const result = await U_CONTROLLER.deleteUser(data);
        if(result == 200){
            // return <Navigate  to='/log'/>;
        }
        console.log(result);
    }

	const onFormSubmit = async (e) => {
        e.preventDefault();
        this.validate();

        var data = {
            id: this.state.id,
            fristname: this.state.fristname,
            phone: this.state.phone,
            email: this.state.email,
            password: this.state.password,
            birthday: this.state.birthday,
            address: this.state.address,
            image: this.state.image,
        }

        const result = await U_CONTROLLER.editUser(data)

        console.log(result);

        if(result == 200){
            CONFIG.showAlert("Successfully Updated");
            document.getElementById("cancelBtn").click();
            //this.clear();
        }
    }

	
	let navigate = useNavigate(); 
	const editPage = () =>{ 
		let path = `/editProfile/` + userData?._id; 

		navigate(path);
	  }
 return (
      <div className="container">
<div className="row gutters">
<div className="col-xl-3 col-lg-3 col-md-12 col-sm-12 col-12">
<div className="card h-100">
	<div className="card-body">
		<div className="account-settings">
			<div className="user-profile">
				<div className="user-avatar">
				<img className={"imageStyle"} src={userData?.image} alt="UserImage" />
				</div>
				<h5 className="user-name">email</h5>
				<h6 className="user-name">{userData?.email}</h6>
			</div>
		</div>
	</div>
</div>
</div>
<div className="col-xl-9 col-lg-9 col-md-12 col-sm-12 col-12">
<div className="card h-100">
	<div className="card-body">
		<div className="row gutters">
			<div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
				<h6 className="mb-2 text-primary">Profile Update</h6>
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="fullName">Full Name</label>
					<input type="text" className="form-control" id="fullName" placeholder="Enter full name" />
				</div>
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="eMail">Email</label>
					<input type="email" className="form-control" id="eMail" placeholder="Enter email ID" value={userData?.email} />
				</div>
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="phone">Phone</label>
					<input type="text" className="form-control" id="phone" placeholder="Enter phone number" value={userData?._id}/>
				</div>
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="website">Birthday</label>
					<input type="date" className="form-control" id="website" placeholder="dd/mm/yy"/>
				</div>
			</div>
		</div>
		<div className="row gutters">
			<div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="Street">Address</label>
					<input type="name" className="form-control" id="Street" placeholder="Enter Street" />
				</div>
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="ciTy">Password</label>
					<input type="password" className="form-control" id="ciTy" placeholder="Enter City" />
				</div>
			</div>
			
			
		</div>
		<div className="row gutters">
			<div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
				<div className="text-right">
					<button type="button" id="submit" name="submit" className="btn btn-secondary" onClick={onDelete} >Delete</button>
					<Link to={"/editProfile/" + userData?._id}><button type="button" id="submit" name="submit" className="btn btn-primary" >Update</button></Link>
				</div>
			</div>
		</div>
	</div>
</div>
</div>
</div>
</div>
    
    )
  }

  export default Profile
