import React from 'react';

import {FormInput , FormSelect , MultiFormSelect} from '../../components/Form/Form'
import { useParams } from 'react-router-dom'
import { Link } from 'react-router-dom';

import PACK_CONTROLLER from '../../Controllers/User.Controller';
import CONFIG from '../../Controllers/Config.Controller';


function Profile(){

const { id } = useParams();
const result = PACK_CONTROLLER.getOne((p) => p._id === (id));
console.log("User results: ", result);

return result;

}
class EditProfile extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            fristname: '',
            phone: '',
            email: '',
            password: '',
            birthday: '',
            address: '',
            image: '',
            id: '',
            errors : { 
                fristname : false , 
                phone : false ,
                email : false ,
                password : false ,
                birthday : false ,
                address : false ,
                image : false ,
            }
        };
    }

    async componentDidMount() {
        
        // console.log("User ID: ", this.props.match.params.id);
        
        const result = Profile();

        console.log("User results: ", result);

        this.setState({
            id: result.id,
            fristname: result.fristname,
            phone: result.phone,
            email: result.email,
            password: result.password,
            birthday: result.birthday,
            address: result.address,
            image: result.image
            
        })
    }

    formValueChange = (e) => {
        this.setState({[e.target.name] : e.target.value  });
    }

    onFormSubmit = async (e) => {
        e.preventDefault();
        this.validate();

        var data = {
            id: this.state.id,
            fristname: this.state.fristname,
            phone: this.state.phone,
            email: this.state.email,
            password: this.state.password,
            birthday: this.state.birthday,
            address: this.state.address,
            image: this.state.image,
        }

        const result = await PACK_CONTROLLER.editUser(data)

        console.log(result);

        if(result == 200){
            CONFIG.showAlert("Successfully Updated");
            document.getElementById("cancelBtn").click();
            //this.clear();
        }
    }

    render(){
        const {errors} = this.state;
    return (
        <div className="app" >
       
        <main>
            <div className="container-fluid" >
            <div className="row" >
                <div className="col-12 shadow-sm rounded bg-white mt-1" >
                    <h6 className="text-header py-3 mb-0 font-weight-bold line-hight-1">Edit Package<br></br>
                    <span className="text-muted small">Editing package details</span></h6>
                </div>
                <div className="col-12 shadow-sm rounded bg-white mt-3 pb-1" >
                <form onSubmit={(e) => this.onFormSubmit(e)}>
                <div className="row mt-1 pb-3" >
                <div className="col-md-6 mt-1 mb-1" >
                            <FormInput 
                                label={'Name'}
                                placeholder={'Enter package name'}
                                error={ errors.fristname}
                                name="fristname"
                                value={this.state.fristname}
                                onChange={this.formValueChange}
                                error_meesage={'*Package name is required'}
                            />
                    </div>
                    <div className="col-md-6 mt-1 mb-1" >
                            <FormInput 
                                label={'Package Product'}
                                placeholder={'Enter package product'}
                                error={ errors.phone}
                                name="phone"
                                value={this.state.phone}
                                onChange={this.formValueChange}
                                error_meesage={'*Package produc is required'}
                            />
                    </div>
                    <div className="col-md-6 mt-1 mb-1" >
                            <FormInput 
                                label={'Price'}
                                placeholder={'Enter price'}
                                error={ errors.email}
                                name="email"
                                value={this.state.email}
                                onChange={this.formValueChange}
                                error_meesage={'*Price is required'}
                            />
                    </div>
                    <div className="col-md-6 mt-1 mb-1" >
                        <FormSelect 
                                label={'Duration'}
                                options={AT_OPTIONS}
                                error={ errors.password}
                                selected={this.state.password}
                                onChange={this.formValueChange}
                                name="password"
                                error_meesage={'*password is required'}
                            />
                    </div>
                    <div className="col-md-6 mt-1 mb-1" >
                            <FormInput 
                                label={'Quntity'}
                                placeholder={'Enter birthday'}
                                error={ errors.birthday}
                                name="birthday"
                                value={this.state.birthday}
                                onChange={this.formValueChange}
                                error_meesage={'*Number of quntity is required'}
                            />
                    </div>
                    <div className="col-md-12 mt-1 mb-1" >
                            <button type="submit" className="btn-outline-primary mt-2 btn btn-sm px-2 mr-4">Update</button>
                            <Link to="/package/list"><button id="cancelBtn" className="btn-outline-secondary mt-2 btn btn-sm px-2 ">Cancel</button></Link>
                    </div>
                </div>
                </form>
                </div>
            </div>
            </div>
        </main>
    </div>
    );}

    validate = () => {
        let { packageName, packageProduct, price, duration, quntity} = this.state;
        let count = 0;
        let errors = {}

        if( packageName == ''){
            errors.packageName = true
            count++
        }else{
            errors.packageName = false 
        }

        if( packageProduct == ''){
            errors.packageProduct = true
            count++
        }else{
            errors.packageProduct = false 
        }

        if( price == ''){
            errors.price = true
            count++
        }else{
            errors.price = false 
        }

        if( duration == ''){
            errors.duration = true
            count++
        }else{
            errors.duration = false 
        }

        if( quntity == ''){
            errors.quntity = true
            count++
        }else{
            errors.quntity = false 
        }
       
        this.setState({errors})
        return count == 0;
    }


    clear = ()=>{
        this.setState(
            {
                fristname: '',
                phone: '',
                email: '',
                password: '',
                birthday: '',
                address: '',
                image: ''
            }
        )
    }
}

const AT_OPTIONS = [{ label : 'Select Access Time' ,value : "" } , 
...['1 month', '2 months', '3 months', '4 months'].map( i => {
    return{
        label :  i  ,
         value : i 
    }
})];

export default EditProfile;
