import './profileUpdate.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import {isLength, isMatch} from './validation'
import {showSuccessMsg, showErrMsg} from '../Notification'
import {fetchUser} from '../../redux/action/authAction'



function Profile(){

	const[userData , setUserData] = useState(null);

	useEffect(()=> {
		const token = sessionStorage.getItem('token')
		async function getUser() {
			const userdata =await fetchUser(token);
			console.log("asasas" , userdata);
			setUserData(userdata.data)
		}
		getUser();
		console.log("uD" , userData);
	}, [])

	onFormSubmit = async (e) => {
	editProduct = async (data) => {
        console.log(data);



        var resp = 600;
        var userData = {}
        await Axios.put(
            `/user/update`,
            data
        )
            .then(Response => {
                console.log(Response);

                resp = Response.status;
                userData = Response.data.userData
            })
            .catch(err => {
                console.error(err);
                try {
                    console.error(err);
                    resp = err.response.status;
                } catch (error) {
                    console.log(error);
                    resp = 600;
                }
            });

        if (resp === 200) {
            return resp;
        }
        return resp;
    }
}

	
 return (
      <div className="container">
<div className="row gutters">
<div className="col-xl-3 col-lg-3 col-md-12 col-sm-12 col-12">
<div className="card h-100">
	<div className="card-body">
		<div className="account-settings">
			<div className="user-profile">
				<div className="user-avatar">
					<img src="https://bootdey.com/img/Content/avatar/avatar6.png"  alt="Maxwell Admin"/>
				</div>
				<h5 className="user-name">email</h5>
				<h6 className="user-name">{userData?.email}</h6>
			</div>
		</div>
	</div>
</div>
</div>
<div className="col-xl-9 col-lg-9 col-md-12 col-sm-12 col-12">
<div className="card h-100">
	<div className="card-body">
		<div className="row gutters">
			<div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
				<h6 className="mb-2 text-primary">Profile Update</h6>
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="fullName">Full Name</label>
					<input type="text" className="form-control" id="fullName" placeholder="Enter full name" value={userData?.fristname}/>
				</div>
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="eMail">Email</label>
					<input type="email" className="form-control" id="eMail" placeholder="Enter email ID" value={userData?.email} />
				</div>
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="phone">Phone</label>
					<input type="text" className="form-control" id="phone" placeholder="Enter phone number" value={userData?.fristname}/>
				</div>
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="website">Birthday</label>
					<input type="date" className="form-control" id="website" placeholder="dd/mm/yy" value={userData?.fristname}/>
				</div>
			</div>
		</div>
		<div className="row gutters">
			<div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="Street">Address</label>
					<input type="name" className="form-control" id="Street" placeholder="Enter Street" />
				</div>
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="ciTy">Password</label>
					<input type="password" className="form-control" id="ciTy" placeholder="Enter City" />
				</div>
			</div>
			
			
		</div>
		<div className="row gutters">
			<div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
				<div className="text-right">
					<button type="button" id="submit" name="submit" className="btn btn-secondary">Delete</button>
					<button type="button" id="submit" name="submit" className="btn btn-primary" onClick={onFormSubmit}>Update</button>
				</div>
			</div>
		</div>
	</div>
</div>
</div>
</div>
</div>
    
    )
  }

  export default Profile
