import './profileUpdate.css';
import React, { useState, useEffect } from 'react';

import { Navigate , useNavigate  } from "react-router-dom";
import { Link } from 'react-router-dom';

import {fetchUser} from '../../redux/action/authAction'
import U_CONTROLLER from '../../Controllers/User.Controller';




function Profile(){

	const[userData , setUserData] = useState(null);


	useEffect(()=> {
		const token = sessionStorage.getItem('token')
		async function getUser() {
			const userdata =await fetchUser(token);
			console.log("asasas" , userdata);
			setUserData(userdata.data)
		}
		getUser();
		console.log("uD" , userData);
	}, [])


	const fetchUser = async(userId) => {
		const res = await dispatch(getUser(userId));
		user = res;
		form.setFieldsValue(res)
		setLoading(false);
	}
	  useEffect(() => {
		if(user) {
		  form.setFieldsValue(user)
		} else {
		  setLoading(true);
		  fetchUser(id)
		}
	  }, [])
  
	  function onPasswordChange(checked) {
		setHidePass(checked);
	  }
  
	  const tailFormItemLayout = {
		wrapperCol: {
			xs: {
			span: 24,
			offset: 0,
			},
			sm: {
			span: 16,
			offset: 8,
			},
		},
	  };
  
	const [form] = Form.useForm();
	const [passForm] = Form.useForm();
  
	const onFinish = async(values) => {
	  setLoadingBtn(true)
	  let updatedUser;
	  if (filePath) {
		updatedUser = {id, ...values , profile_photo: filePath};
	  } else {
		updatedUser = {id, ...values , profile_photo: user?.profile_photo};
	  }
	  const res = await dispatch(updateUser(updatedUser))
  
	  if (res.status === 200) {
		if (user) {
		  userUpdate({...updatedUser, regNumber: user.regNumber, role: user.role})
		}
		message.success("Profile Updated Successfully")
	  } else if (res.status === 401) {
		message.error("Your Password is wrong")
	  }else {
		message.error("An Error Occurred")
	  }
	  setLoadingBtn(false)
	};
  
	const onPasswordUpdate = async(values) => {
	  setLoadingPass(true)
	  const updatePass = {id, ...values};
	  const res = await dispatch(updatePassword(updatePass));
	  if (res.status === 200) {
		message.success("Password Updated Successfully")
	  } else {
		message.error(res.data)
	  }
	  setLoadingPass(false)
	}
  
	const props = {
	  name: 'file',
	  multiple: false,
	  action: 'http://localhost:5000/api/file',
	  maxCount: 1,
	  onChange(info) {
		const { status } = info.file;
		if (status !== 'uploading') {
			if (info.fileList.length > 0) {
			  setFilePath(info.file.response)
		  } else {
			  setFilePath('')
		  }
		}
		if (status === 'done') {
		  message.success(`${info.file.name} file uploaded successfully.`);
		} else if (status === 'error') {
		  message.error(`${info.file.name} file upload failed.`);
		}
	  },
	};

	const onDelete = async () => {
        const data = {
            id: userData?._id,
            name: userData?.email,
        }
        const result = await U_CONTROLLER.deleteUser(data);
        if(result == 200){
            // return <Navigate  to='/log'/>;
        }
        console.log(result);
    }
	let navigate = useNavigate(); 
	const editPage = () =>{ 
		let path = `/editProfile/` + userData?._id; 

		navigate(path);
	  }
 return (
      <div className="container">
<div className="row gutters">
<div className="col-xl-3 col-lg-3 col-md-12 col-sm-12 col-12">
<div className="card h-100">
	<div className="card-body">
		<div className="account-settings">
			<div className="user-profile">
				<div className="user-avatar">
				<img className={"imageStyle"} src={userData?.image} alt="UserImage" />
				</div>
				<h5 className="user-name">email</h5>
				<h6 className="user-name">{userData?.email}</h6>
			</div>
		</div>
	</div>
</div>
</div>
<div className="col-xl-9 col-lg-9 col-md-12 col-sm-12 col-12">
<div className="card h-100">
	<div className="card-body">
		<div className="row gutters">
			<div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
				<h6 className="mb-2 text-primary">Profile Update</h6>
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="fullName">Full Name</label>
					<input type="text" className="form-control" id="fullName" placeholder="Enter full name" value={userData?.fristname}/>
				</div>
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="eMail">Email</label>
					<input type="email" className="form-control" id="eMail" placeholder="Enter email ID" value={userData?.email} />
				</div>
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="phone">Phone</label>
					<input type="text" className="form-control" id="phone" placeholder="Enter phone number" value={userData?.phone}/>
				</div>
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="website">Birthday</label>
					<input type="date" className="form-control" id="website" placeholder="dd/mm/yy"value={userData?.birthday}/>
				</div>
			</div>
		</div>
		<div className="row gutters">
			<div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="Street">Address</label>
					<input type="name" className="form-control" id="Street" placeholder="Enter Street"value={userData?.address} />
				</div>
			</div>
			<div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
				<div className="form-group">
					<label for="ciTy">Role</label>
					<input type="role" className="form-control" id="role" placeholder="role is not avilabele" value={userData?.role}/>
				</div>
			</div>
			
			
		</div>
		<div className="row gutters">
			<div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
				<div className="text-right">
					<button type="button" id="submit" name="submit" className="btn btn-secondary" onClick={onDelete} >Delete</button>
					<Link to={"/editProfile/" + userData?._id}><button type="button" id="submit" name="submit" className="btn btn-primary" >Update</button></Link>
					
				</div>
			</div>
		</div>
	</div>
</div>
</div>
</div>
</div>
    
    )
  }

  export default Profile
